import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { UserRegistration } from '../services/RegistrationService';
import axios from 'axios';

export default class Registration extends Component{
    constructor(props){
        super(props);

        this.state = {
            isLoading : true,
            token:'',
            RegistrationUsername:'',
            RegistrationEmail:'',
            RegistrationPassword:'',
            register: false,
            error: false,
        };

        this.onTextboxChangeRegistrationUsername = this.onTextboxChangeRegistrationUsername.bind(this);
        this.onTextboxChangeRegistrationEmail = this.onTextboxChangeRegistrationEmail.bind(this);
        this.onTextboxChangeRegistrationPassword = this.onTextboxChangeRegistrationPassword.bind(this);
        this.onSignUp = this.onSignUp.bind(this);
    }

    componentDidMount() {
        this.setState({
          isLoading: false
        });
    };

    onTextboxChangeRegistrationUsername(event) {
        this.setState({
            RegistrationUsername: event.target.value,
        });
    }

    onTextboxChangeRegistrationEmail(event) {
        this.setState({
            RegistrationEmail: event.target.value,
        });
    }

    onTextboxChangeRegistrationPassword(event) {
        this.setState({
            RegistrationPassword: event.target.value,
        });
    }

    onSignUp() {
        const {
            RegistrationUsername,
            RegistrationEmail,
            RegistrationPassword
        } = this.state;

        this.setState({
          isLoading: true,
        });

        const data = {
            Email:RegistrationEmail,
            Username: RegistrationUsername,
            Password: RegistrationPassword,
        };

        axios.post('/users/register', {data})
            .then(res => {
                console.log('json', res.json);
                if (json.success) {
                    this.setState ({
                        RegistrationUsername:'',
                        RegistrationEmail:'',
                        RegistrationPassword:'',
                        register: true,
                        error: false,
                        isLoading:false,
                });
                } else
                    this.setState ({
                    error: true,
                    register: false,
                    isLoading:false,
                });
            // .catch(function (error) {
            //     console.log(error);
            // });
        }
        

        // const registerStatus = UserRegistration (data);
        // if (registerStatus === 200) {
        // this.setState ({
        //     RegistrationUsername:'',
        //     RegistrationEmail:'',
        //     RegistrationPassword:'',
        //     register: true,
        //     error: false,
        //     isLoading:false,
        // });
        // } else
        // this.setState ({
        //     error: true,
        //     register: false,
        //     isLoading:false,
        // });

            )};

    render() {

        const {
          isLoading,
          token,
          register,
          error,
          RegistrationUsername,
          RegistrationEmail,
          RegistrationPassword,
        } = this.state;

        if (isLoading) {
            return (<div><p>Loading...</p></div>);
        }

        return(
            <div className="Registration">
                <input
                type="username"
                placeholder="Username"
                value={RegistrationUsername}
                onChange={this.onTextboxChangeRegistrationUsername}
                /><br />
                <input
                type="email"
                placeholder="Email"
                value={RegistrationEmail}
                onChange={this.onTextboxChangeRegistrationEmail}
                /><br />
                <input
                type="password"
                placeholder="Password"
                value={RegistrationPassword}
                onChange={this.onTextboxChangeRegistrationPassword}
                /><br />
                <button onClick={this.onSignUp}>Sign Up</button>
            </div>

        );
    }

};
